import React, { useRef } from "react";

export default function MySongs({ songs }) {
  const audioRefs = useRef({});

  // Default demo tracks
  const defaultSongs = [
    {
      title: "City Nights",
      artist: "Echo Wave",
      details: "Vocals: +2 dB\nDrums: -3 dB\nBass: 0 dB\nOther: -1 dB",
      url: "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-1.mp3",
    },
    {
      title: "Dreamline",
      artist: "Solar Horizon",
      details: "Vocals: 0 dB\nDrums: +4 dB\nBass: -2 dB\nOther: +1 dB",
      url: "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-2.mp3",
    },
    {
      title: "Midnight Drive",
      artist: "Aural Flow",
      details: "Vocals: -1 dB\nDrums: 0 dB\nBass: +3 dB\nOther: -2 dB",
      url: "https://www.soundhelix.com/examples/mp3/SoundHelix-Song-3.mp3",
    },
  ];

  const allSongs = songs && songs.length > 0 ? songs : defaultSongs;

  const handlePlay = (url, idx) => {
    // Stop any currently playing track
    Object.values(audioRefs.current).forEach((a) => {
      if (a && !a.paused) a.pause();
    });

    if (!audioRefs.current[idx]) {
      audioRefs.current[idx] = new Audio(url);
    }
    audioRefs.current[idx].currentTime = 0;
    audioRefs.current[idx].play();
  };

  const handleStop = (idx) => {
    const audio = audioRefs.current[idx];
    if (audio) {
      audio.pause();
      audio.currentTime = 0;
    }
  };

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-lg font-semibold mb-4 text-center">My Songs</h2>

      <div className="space-y-4">
        {allSongs.map((song, idx) => (
          <div
            key={idx}
            className="bg-[#1f212d] p-4 rounded-lg border border-gray-700"
          >
            <h3 className="text-md font-medium text-white">{song.title}</h3>
            <p className="text-sm text-gray-400 mb-2">{song.artist}</p>

            <pre className="text-xs text-gray-300 bg-[#2b2d3a] p-2 rounded-md overflow-x-auto">
              {song.details}
            </pre>

            <div className="mt-3 flex gap-3">
              <button
                onClick={() => handlePlay(song.url, idx)}
                className="px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md text-sm"
              >
                Play
              </button>
              <button
                onClick={() => handleStop(idx)}
                className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-md text-sm"
              >
                Stop
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
