import React, { useState, useRef } from "react";
import { separateAudio } from "../api";

const channels = [
  { key: "vocals", label: "ðŸŽ¤ Vocals", color: "from-blue-400 to-blue-600" },
  { key: "guitar", label: "ðŸŽ¸ Guitar", color: "from-purple-400 to-purple-600" },
  { key: "bass", label: "ðŸŽ¶ Bass", color: "from-green-400 to-green-600" },
  { key: "drums", label: "ðŸ¥ Drums", color: "from-yellow-400 to-yellow-600" },
  { key: "piano", label: "ðŸŽ¹ Piano", color: "from-pink-400 to-pink-600" },
];

const fmt = (db) => (db > 0 ? `+${db}` : `${db}`) + " dB";
const dbToGain = (db) => Math.pow(10, db / 20);

export default function Mixer({ onSave }) {
  const [gains, setGains] = useState(Object.fromEntries(channels.map((c) => [c.key, 0])));
  const [file, setFile] = useState(null);
  const [separating, setSeparating] = useState(false);
  const [stems, setStems] = useState({});
  const [playing, setPlaying] = useState(false);
  const audioCtxRef = useRef(null);

  const handleChange = (key, val) => setGains((prev) => ({ ...prev, [key]: parseInt(val) }));

  const handleUpload = async (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setSeparating(true);
    try {
      const result = await separateAudio(f);
      // Map stems by label
      const stemMap = Object.fromEntries(result.labels.map((lbl, i) => [lbl, result.stems[i]]));
      setStems(stemMap);
    } catch (e) {
      console.error(e);
      alert("Error running Demucs separation");
    } finally {
      setSeparating(false);
    }
  };

  const playMix = async () => {
    if (!stems || Object.keys(stems).length === 0) {
      alert("No stems available. Please upload a file first.");
      return;
    }

    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    audioCtxRef.current = ctx;
    const stemEntries = Object.entries(stems);
    const sources = [];

    for (const [key, url] of stemEntries) {
      try {
        const resp = await fetch(`http://127.0.0.1:8000/${url}`);
        const arrayBuf = await resp.arrayBuffer();
        const buffer = await ctx.decodeAudioData(arrayBuf);
        const src = ctx.createBufferSource();
        src.buffer = buffer;
        const gainNode = ctx.createGain();
        gainNode.gain.value = dbToGain(gains[key] ?? 0);
        src.connect(gainNode).connect(ctx.destination);
        sources.push(src);
      } catch (err) {
        console.error("Failed to load stem:", key, err);
      }
    }

    sources.forEach((src) => src.start());
    setPlaying(true);

    // Stop after longest buffer
    const duration = Math.max(...sources.map((s) => s.buffer.duration));
    setTimeout(() => {
      setPlaying(false);
      ctx.close();
    }, duration * 1000);
  };

  const handleSave = () => {
    if (!file) {
      alert("Please upload a music file first.");
      return;
    }
    const title = file.name.replace(/\.[^/.]+$/, "");
    const details = channels
      .map((c) => `${c.label.split(" ")[0]} ${fmt(gains[c.key])}`)
      .join("\n");
    onSave?.({ title, artist: "Custom Mix", details });
  };

  return (
    <div className="relative bg-[#151931]/70 backdrop-blur-lg border border-[#2a2f4a] rounded-2xl p-6 shadow-lg">
      <div className="absolute inset-0 bg-gradient-to-br from-[#243066]/20 to-[#0f1220]/10 pointer-events-none rounded-2xl"></div>

      {/* Upload section */}
      <div className="relative z-10 mb-6 flex flex-col sm:flex-row sm:items-center gap-3">
        <label className="text-sm font-semibold text-gray-300">
          ðŸŽ§ Choose Audio File:
        </label>
        <input
          type="file"
          accept="audio/*"
          onChange={handleUpload}
          className="text-sm bg-[#0e1226]/80 border border-[#2a2f4a] p-2 rounded-md text-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
        />
        {file && (
          <p className="text-xs text-gray-400 truncate max-w-[200px]">
            {file.name}
          </p>
        )}
        {separating && (
          <div className="text-sm text-blue-300 animate-pulse ml-auto">
            Separating with Demucsâ€¦
          </div>
        )}
      </div>

      {/* Sliders */}
      <div className="grid grid-cols-2 sm:grid-cols-5 gap-6 justify-items-center">
        {channels.map((ch) => (
          <div
            key={ch.key}
            className="flex flex-col items-center bg-[#0e1226]/60 backdrop-blur-sm px-3 py-4 rounded-xl border border-[#2a2f4a] hover:border-blue-500 transition"
          >
            <span
              className={`text-center mb-2 bg-clip-text text-transparent bg-gradient-to-r ${ch.color} font-semibold`}
            >
              {ch.label}
            </span>
            <div className="relative flex items-center justify-center h-[220px]">
              <input
                type="range"
                min="-24"
                max="12"
                value={gains[ch.key]}
                onChange={(e) => handleChange(ch.key, e.target.value)}
                className="appearance-none w-[220px] h-[6px] bg-transparent rotate-[-90deg] cursor-pointer"
              />
              <div className="absolute w-[6px] h-[200px] bg-[#0a0d1c] rounded-full border border-[#2a2f4a] overflow-hidden">
                <div
                  className="absolute bottom-0 left-0 w-full bg-gradient-to-t from-blue-500 to-cyan-300 rounded-full transition-all duration-300"
                  style={{
                    height: `${((parseInt(gains[ch.key]) + 24) / 36) * 100}%`,
                  }}
                ></div>
              </div>
            </div>
            <p className="text-xs mt-2 text-gray-300 tracking-wide">
              {fmt(gains[ch.key])}
            </p>
          </div>
        ))}
      </div>

      {/* Control buttons */}
      <div className="flex justify-center gap-4 mt-6">
        <button
          onClick={playMix}
          disabled={separating || playing}
          className={`px-5 py-2 rounded-md font-semibold transition shadow-md ${
            playing
              ? "bg-green-400 text-black scale-105"
              : "bg-blue-500 hover:bg-blue-600 text-black"
          }`}
        >
          {playing ? "ðŸ”Š Playing..." : "â–¶ Preview Mix"}
        </button>
        <button
          onClick={handleSave}
          className="px-5 py-2 bg-indigo-500 hover:bg-indigo-600 text-black font-semibold rounded-md shadow-md"
        >
          ðŸ’¾ Save Mix
        </button>
      </div>
    </div>
  );
}
