import React, { useState, useRef } from "react";

const channels = [
  { key: "vocals", label: "üé§ Vocals", color: "from-blue-400 to-blue-600" },
  { key: "drums", label: "ü•Å Drums", color: "from-yellow-400 to-yellow-600" },
  { key: "bass", label: "üé∂ Bass", color: "from-green-400 to-green-600" },
  { key: "other", label: "üéß Other", color: "from-purple-400 to-purple-600" },
];

const fmt = (db) => (db > 0 ? `+${db}` : `${db}`) + " dB";
const dbToGain = (db) => Math.pow(10, db / 20);

export default function Mixer({ onSave }) {
  const [gains, setGains] = useState(Object.fromEntries(channels.map((c) => [c.key, 0])));
  const [file, setFile] = useState(null);
  const [separating, setSeparating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [paused, setPaused] = useState(false);
  const [sessionId, setSessionId] = useState(null);

  const audioCtxRef = useRef(null);
  const sourcesRef = useRef([]);
  const startTimeRef = useRef(0);
  const pausedAtRef = useRef(0);

  // ------------------------
  // Handle slider changes
  // ------------------------
  const handleChange = (key, val) => setGains((prev) => ({ ...prev, [key]: parseInt(val) }));

  // ------------------------
  // Upload file + start Demucs separation
  // ------------------------
  const handleUpload = async (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setSeparating(true);
    setProgress(0);

    const formData = new FormData();
    formData.append("file", f);

    try {
      const startRes = await fetch("http://127.0.0.1:8000/start_separation", {
        method: "POST",
        body: formData,
      });
      const { session_id } = await startRes.json();
      setSessionId(session_id);

      // --- Live progress via SSE ---
      const es = new EventSource(`http://127.0.0.1:8000/progress/${session_id}`);
      es.onmessage = (ev) => {
        const val = parseInt(ev.data);
        setProgress(val);
        if (val >= 100) {
          es.close();
          setSeparating(false);
          alert("‚úÖ Separation complete!");
        }
      };
      es.onerror = () => {
        es.close();
        setSeparating(false);
        alert("‚ùå Error receiving progress updates.");
      };
    } catch (err) {
      console.error("Upload error:", err);
      setSeparating(false);
      alert("Error starting separation");
    }
  };

  // ------------------------
  // Pause/Resume backend processing
  // ------------------------
  const handlePauseBackend = async () => {
    if (!sessionId) return;
    await fetch(`http://127.0.0.1:8000/pause/${sessionId}`, { method: "POST" });
  };
  const handleResumeBackend = async () => {
    if (!sessionId) return;
    await fetch(`http://127.0.0.1:8000/resume/${sessionId}`, { method: "POST" });
  };

  // ------------------------
  // Play stems (preview mix)
  // ------------------------
  const playMix = async () => {
    try {
      // Send gains to backend for mixing
      const formData = new FormData();
      formData.append("vocals_gain", gains.vocals);
      formData.append("drums_gain", gains.drums);
      formData.append("bass_gain", gains.bass);
      formData.append("other_gain", gains.other);

      const resp = await fetch("http://127.0.0.1:8000/mix", {
        method: "POST",
        body: formData,
      });
      const data = await resp.json();
      const mixUrl = `http://127.0.0.1:8000${data.path}`;

      // Play mixed output
      const audio = new Audio(mixUrl);
      audioRef.current = audio;
      audio.play();
      setPlaying(true);
      setPaused(false);

      audio.onended = () => {
        setPlaying(false);
        setPaused(false);
      };
    } catch (err) {
      console.error("Mix playback error:", err);
      alert("Error mixing stems");
    }
  };


  // ------------------------
  // Pause/Resume audio playback
  // ------------------------
  const pauseMix = async () => {
    if (!audioCtxRef.current) return;
    const ctx = audioCtxRef.current;
    pausedAtRef.current = ctx.currentTime - startTimeRef.current;
    ctx.suspend();
    setPaused(true);
  };

  const resumeMix = async () => {
    if (!audioCtxRef.current) return;
    await audioCtxRef.current.resume();
    setPaused(false);
  };

  const stopMix = async () => {
    if (audioCtxRef.current) {
      sourcesRef.current.forEach((src) => src.stop());
      await audioCtxRef.current.close();
      setPlaying(false);
      setPaused(false);
    }
  };

  // ------------------------
  // Save mix info
  // ------------------------
  const handleSave = () => {
    if (!file) {
      alert("Please upload a file first.");
      return;
    }
    const title = file.name.replace(/\.[^/.]+$/, "");
    const details = channels
      .map((c) => `${c.label.split(" ")[0]} ${fmt(gains[c.key])}`)
      .join("\n");
    onSave?.({ title, artist: "Custom Mix", details });
  };

  // ------------------------
  // UI
  // ------------------------
  return (
    <div className="relative bg-[#151931]/70 backdrop-blur-lg border border-[#2a2f4a] rounded-2xl p-6 shadow-lg">
      <div className="absolute inset-0 bg-gradient-to-br from-[#243066]/20 to-[#0f1220]/10 pointer-events-none rounded-2xl"></div>

      {/* Upload */}
      <div className="relative z-10 mb-6 flex flex-col sm:flex-row sm:items-center gap-3 w-full">
        <label className="text-sm font-semibold text-gray-300">
          üéß Choose Audio File:
        </label>
        <input
          type="file"
          accept="audio/*"
          onChange={handleUpload}
          className="text-sm bg-[#0e1226]/80 border border-[#2a2f4a] p-2 rounded-md text-gray-200 focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
        />
        {file && (
          <p className="text-xs text-gray-400 truncate max-w-[200px]">
            {file.name}
          </p>
        )}
      </div>

      {/* Progress bar + backend pause/resume */}
      {separating && (
        <div className="w-full mb-6">
          <div className="w-full bg-[#0a0d1c]/50 rounded-md h-2 overflow-hidden">
            <div
              className="h-full bg-gradient-to-r from-blue-500 to-cyan-300 transition-all duration-500"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <div className="mt-3 flex justify-center gap-3">
            <button
              onClick={handlePauseBackend}
              className="px-3 py-1 rounded bg-gray-700 hover:bg-gray-600 text-sm"
            >
              ‚è∏ Pause
            </button>
            <button
              onClick={handleResumeBackend}
              className="px-3 py-1 rounded bg-gray-700 hover:bg-gray-600 text-sm"
            >
              ‚ñ∂ Resume
            </button>
            <span className="text-xs text-gray-400 ml-3">
              {progress < 100 ? `Separating... ${progress}%` : "Done!"}
            </span>
          </div>
        </div>
      )}

      {/* Sliders */}
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-6 justify-items-center">
        {channels.map((ch) => (
          <div
            key={ch.key}
            className="flex flex-col items-center bg-[#0e1226]/60 backdrop-blur-sm px-3 py-4 rounded-xl border border-[#2a2f4a] hover:border-blue-500 transition"
          >
            <span
              className={`text-center mb-2 bg-clip-text text-transparent bg-gradient-to-r ${ch.color} font-semibold`}
            >
              {ch.label}
            </span>

            <div className="relative flex items-center justify-center h-[220px]">
              <input
                type="range"
                min="-24"
                max="12"
                value={gains[ch.key]}
                onChange={(e) => handleChange(ch.key, e.target.value)}
                className="appearance-none w-[220px] h-[6px] bg-transparent rotate-[-90deg] cursor-pointer"
              />
              <div className="absolute w-[6px] h-[200px] bg-[#0a0d1c] rounded-full border border-[#2a2f4a] overflow-hidden">
                <div
                  className="absolute bottom-0 left-0 w-full bg-gradient-to-t from-blue-500 to-cyan-300 rounded-full transition-all duration-300"
                  style={{
                    height: `${((parseInt(gains[ch.key]) + 24) / 36) * 100}%`,
                  }}
                ></div>
              </div>
            </div>

            <p className="text-xs mt-2 text-gray-300 tracking-wide">
              {fmt(gains[ch.key])}
            </p>
          </div>
        ))}
      </div>

      {/* Control Buttons */}
      <div className="flex justify-center gap-4 mt-6 flex-wrap">
        {!playing && (
          <button
            onClick={playMix}
            className="px-5 py-2 bg-blue-500 hover:bg-blue-600 text-black font-semibold rounded-md shadow-md"
          >
            ‚ñ∂ Play Mix
          </button>
        )}

        {playing && !paused && (
          <button
            onClick={pauseMix}
            className="px-5 py-2 bg-yellow-400 hover:bg-yellow-500 text-black font-semibold rounded-md shadow-md"
          >
            ‚è∏ Pause Audio
          </button>
        )}

        {paused && (
          <button
            onClick={resumeMix}
            className="px-5 py-2 bg-green-400 hover:bg-green-500 text-black font-semibold rounded-md shadow-md"
          >
            ‚ñ∂ Resume Audio
          </button>
        )}

        {playing && (
          <button
            onClick={stopMix}
            className="px-5 py-2 bg-red-500 hover:bg-red-600 text-white font-semibold rounded-md shadow-md"
          >
            ‚èπ Stop
          </button>
        )}

        <button
          onClick={handleSave}
