import React, { useState } from "react";
import { separateAudio } from "../api";

const channels = [
  { key: "vocals", label: "ðŸŽ¤ Vocals" },
  { key: "guitar", label: "ðŸŽ¸ Guitar" },
  { key: "bass", label: "ðŸŽ¶ Bass" },
  { key: "drums", label: "ðŸ¥ Drums" },
  { key: "piano", label: "ðŸŽ¹ Piano" },
];

const fmt = (db) => (db > 0 ? `+${db}` : `${db}`) + " dB";

export default function Mixer({ onSave }) {
  const [gains, setGains] = useState(
    Object.fromEntries(channels.map((c) => [c.key, 0]))
  );
  const [file, setFile] = useState(null);
  const [separating, setSeparating] = useState(false);

  const handleChange = (key, val) =>
    setGains((prev) => ({ ...prev, [key]: parseInt(val) }));

  const handleUpload = async (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setSeparating(true);
    try {
      await separateAudio(f);
    } catch (e) {
      // swallow
    } finally {
      setSeparating(false);
    }
  };

  const handleSave = () => {
    if (!file) {
      alert("Please upload a music file first.");
      return;
    }
    const title = file.name.replace(/\.[^/.]+$/, "");
    const details = channels.map((c) => `${c.label.split(' ')[0].trim()} ${fmt(gains[c.key])}`).join("\n");
    onSave?.({ title, artist: "Custom Mix", details });
  };

  return (
    <div className="bg-[#171b2e]/60 backdrop-blur-md p-6 rounded-xl shadow-lg mt-4">
      <div className="mb-4 flex gap-4 items-center">
        <input
          type="file"
          accept="audio/*"
          onChange={handleUpload}
          className="bg-[#0e1226] border border-[#2a2f4a] p-2 rounded-lg"
        />
        {file && <p className="text-sm text-gray-400">{file.name}</p>}
        {separating && <p className="text-sm text-blue-300">Separating with Demucsâ€¦</p>}
      </div>

      <div className="grid grid-cols-5 gap-6">
        {channels.map((ch) => (
          <div key={ch.key} className="flex flex-col items-center">
            <label className="mb-2 text-center">{ch.label}</label>
            <input
              type="range"
              min="-24"
              max="12"
              value={gains[ch.key]}
              onChange={(e) => handleChange(ch.key, e.target.value)}
              className="rotate-[-90deg] w-48 accent-blue-400"
            />
            <p className="text-xs mt-2">{gains[ch.key]} dB</p>
          </div>
        ))}
      </div>

      <button
        onClick={handleSave}
        className="mt-6 bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded-md text-black font-semibold"
      >
        ðŸ’¾ Save Mix
      </button>
    </div>
  );
}
