import React, { useState } from "react";

export default function Landing({ onSelectUser }) {
  const [users, setUsers] = useState(["Alex", "Sam", "Jamie"]);

  const handleAddUser = () => {
    const name = prompt("Enter new username:");
    if (name) setUsers((prev) => [...prev, name]);
  };

  return (
    <div className="text-center mt-16">
      <h2 className="text-2xl font-semibold mb-8 flex items-center justify-center gap-2">
        ðŸŽ§ AudioMix Profiles
      </h2>

      {/* Profile List */}
      <div className="flex justify-center gap-6 flex-wrap mb-6">
        {users.map((user, idx) => (
          <div
            key={idx}
            onClick={() =>
              typeof onSelectUser === "function" && onSelectUser(user)
            } // âœ… Safe function call
            className="bg-gradient-to-br from-blue-400 to-purple-500 text-black font-semibold px-10 py-12 rounded-xl cursor-pointer hover:opacity-90 transition"
          >
            {user}
          </div>
        ))}
      </div>

      {/* Add User Button */}
      <button
        onClick={handleAddUser}
        className="px-5 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-md"
      >
        + Add User
      </button>
    </div>
  );
}
