import React, { useState, useRef } from "react";

const channels = [
  { key: "vocals", label: "ðŸŽ¤ Vocals", color: "from-blue-400 to-blue-600" },
  { key: "drums", label: "ðŸ¥ Drums", color: "from-yellow-400 to-yellow-600" },
  { key: "bass", label: "ðŸŽ¶ Bass", color: "from-green-400 to-green-600" },
  { key: "other", label: "ðŸŽ§ Other", color: "from-purple-400 to-purple-600" },
];

const fmt = (db) => (db > 0 ? `+${db}` : `${db}`) + " dB";

export default function Mixer({ onSave }) {
  const [file, setFile] = useState(null);
  const [gains, setGains] = useState(Object.fromEntries(channels.map((c) => [c.key, 0])));
  const [separating, setSeparating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [playing, setPlaying] = useState(false);

  const audioRef = useRef(null);
  const username = "alex"; // <-- set username here (or dynamically later)

  // -------------------------------------------------------
  // Handle slider changes
  // -------------------------------------------------------
  const handleChange = (key, val) => setGains((p) => ({ ...p, [key]: parseInt(val) }));

  // -------------------------------------------------------
  // Separation Upload
  // -------------------------------------------------------
  const handleUpload = async (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setSeparating(true);
    setProgress(0);

    const formData = new FormData();
    formData.append("file", f);
    formData.append("username", username);

    try {
      const res = await fetch("http://127.0.0.1:8000/start_separation", {
        method: "POST",
        body: formData,
      });
      if (!res.ok) throw new Error("Separation failed");
      setProgress(100);
      setSeparating(false);
      alert("âœ… Separation complete!");
    } catch (err) {
      console.error(err);
      alert("âŒ Error starting separation");
      setSeparating(false);
    }
  };

  // -------------------------------------------------------
  // Mix and Playback
  // -------------------------------------------------------
  const playMix = async () => {
    try {
      const formData = new FormData();
      formData.append("username", username);
      formData.append("vocals_gain", gains.vocals);
      formData.append("drums_gain", gains.drums);
      formData.append("bass_gain", gains.bass);
      formData.append("other_gain", gains.other);

      const resp = await fetch("http://127.0.0.1:8000/mix", { method: "POST", body: formData });
      if (!resp.ok) throw new Error("Mixing failed");
      const data = await resp.json();

      const mixUrl = `http://127.0.0.1:8000${data.path}`;
      console.log("â–¶ï¸ Playing mix:", mixUrl);
      const audio = new Audio(mixUrl);
      audioRef.current = audio;
      audio.play();
      setPlaying(true);
      audio.onended = () => setPlaying(false);
    } catch (err) {
      console.error(err);
      alert("âŒ Error mixing stems");
    }
  };

  // -------------------------------------------------------
  // Save Mix Info (Frontend list)
  // -------------------------------------------------------
  const handleSave = () => {
    if (!file) {
      alert("Please upload a file first.");
      return;
    }
    const title = file.name.replace(/\.[^/.]+$/, "");
    const details = channels
      .map((c) => `${c.label.split(" ")[0]} ${fmt(gains[c.key])}`)
      .join("\n");
    onSave?.({ title, artist: "Custom Mix", details });
  };

  // -------------------------------------------------------
  // UI Rendering
  // -------------------------------------------------------
  return (
    <div className="relative bg-[#151931]/70 backdrop-blur-lg border border-[#2a2f4a] rounded-2xl p-6 shadow-lg">
      {/* Upload section */}
      <div className="mb-6 flex flex-col sm:flex-row sm:items-center gap-3">
        <label className="text-sm font-semibold text-gray-300">ðŸŽ§ Choose Audio File:</label>
        <input
          type="file"
          accept="audio/*"
          onChange={handleUpload}
          className="text-sm bg-[#0e1226]/80 border border-[#2a2f4a] p-2 rounded-md text-gray-200"
        />
        {file && <p className="text-xs text-gray-400 truncate max-w-[200px]">{file.name}</p>}
      </div>

      {/* Progress bar */}
      {separating && (
        <div className="w-full bg-[#0a0d1c]/50 rounded-md h-2 mb-6 overflow-hidden">
          <div
            className="h-full bg-gradient-to-r from-blue-500 to-cyan-300 transition-all duration-500"
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      )}

      {/* Sliders */}
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-6 justify-items-center">
        {channels.map((ch) => (
          <div
            key={ch.key}
            className="flex flex-col items-center bg-[#0e1226]/60 px-3 py-4 rounded-xl border border-[#2a2f4a]"
          >
            <span
              className={`text-center mb-2 bg-clip-text text-transparent bg-gradient-to-r ${ch.color} font-semibold`}
            >
              {ch.label}
            </span>

            <div className="relative flex items-center justify-center h-[220px]">
              <input
                type="range"
                min="-24"
                max="12"
                value={gains[ch.key]}
                onChange={(e) => handleChange(ch.key, e.target.value)}
                className="appearance-none w-[220px] h-[6px] bg-transparent rotate-[-90deg] cursor-pointer"
              />
              <div className="absolute w-[6px] h-[200px] bg-[#0a0d1c] rounded-full border border-[#2a2f4a] overflow-hidden">
                <div
                  className="absolute bottom-0 left-0 w-full bg-gradient-to-t from-blue-500 to-cyan-300 rounded-full transition-all duration-300"
                  style={{
                    height: `${((parseInt(gains[ch.key]) + 24) / 36) * 100}%`,
                  }}
                ></div>
              </div>
            </div>

            <p className="text-xs mt-2 text-gray-300 tracking-wide">{fmt(gains[ch.key])}</p>
          </div>
        ))}
      </div>

      {/* Buttons */}
      <div className="flex justify-center gap-4 mt-6">
        <button
          onClick={playMix}
          disabled={separating}
          className="px-5 py-2 bg-blue-500 hover:bg-blue-600 text-black font-semibold rounded-md shadow-md"
        >
          â–¶ Play Mix
        </button>

        <button
          onClick={handleSave}
          className="px-5 py-2 bg-indigo-500 hover:bg-indigo-600 text-black font-semibold rounded-md shadow-md"
        >
          ðŸ’¾ Save Mix
        </button>
      </div>
    </div>
  );
}
