import React, { useState } from "react";
import Mixer from "./components/Mixer";
import MySongs from "./components/MySongs";
import AboutModal from "./components/AboutModal";
import HelpModal from "./components/HelpModal";
import Landing from "./components/Landing";

export default function App() {
  const [tab, setTab] = useState("landing");
  const [showAbout, setShowAbout] = useState(false);
  const [showHelp, setShowHelp] = useState(false);

  // ‚úÖ define onSelect handler
  const handleSongSelect = (song) => {
    console.log("‚ñ∂ Selected song:", song.title);
    // Here you could also trigger playback logic if you want
    alert(`Playing "${song.title}" by ${song.artist}`);
    setTab("mixer");
  };

  return (
    <div className="min-h-screen bg-[#0d1020] text-gray-100 p-6">
      <h1 className="text-2xl font-semibold mb-6 text-center">
        üéß Human Audio Mixer (MUSE)
      </h1>

      {/* Navigation Tabs */}
      <div className="flex justify-center gap-4 mb-8 flex-wrap">
        {[
          { key: "landing", label: "Profile" },
          { key: "mixer", label: "Mixer" },
          { key: "mysongs", label: "My Songs" },
        ].map((t) => (
          <button
            key={t.key}
            onClick={() => setTab(t.key)}
            className={`px-5 py-2 rounded-md ${
              tab === t.key
                ? "bg-blue-600 text-white"
                : "bg-gray-700 text-gray-200 hover:bg-gray-600"
            }`}
          >
            {t.label}
          </button>
        ))}

        <button
          onClick={() => setShowHelp(true)}
          className="px-5 py-2 rounded-md bg-gray-700 text-gray-200 hover:bg-gray-600"
        >
          ‚ùì Help
        </button>

        <button
          onClick={() => setShowAbout(true)}
          className="px-5 py-2 rounded-md bg-gray-700 text-gray-200 hover:bg-gray-600"
        >
          ‚ÑπÔ∏è About
        </button>
      </div>

      {/* Render Page */}
      <div className="max-w-6xl mx-auto">
        {tab === "landing" && <Landing />}
        {tab === "mixer" && <Mixer />}
        {tab === "mysongs" && <MySongs onSelect={handleSongSelect} />} {/* ‚úÖ fix */}
      </div>

      {/* Modals */}
      {showAbout && <AboutModal onClose={() => setShowAbout(false)} />}
      {showHelp && <HelpModal onClose={() => setShowHelp(false)} />}
    </div>
  );
}
