import React from "react";

export default function MySongs({ songs }) {
  if (!songs || songs.length === 0) {
    return (
      <div className="text-center text-gray-400 mt-20">
        No songs saved yet. Go to the Mixer tab and create one.
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto">
      <h2 className="text-lg font-semibold mb-4 text-center">My Songs</h2>
      <div className="space-y-4">
        {songs.map((song, idx) => (
          <div
            key={idx}
            className="bg-[#1f212d] p-4 rounded-lg border border-gray-700"
          >
            <h3 className="text-md font-medium">{song.title}</h3>
            <p className="text-sm text-gray-400 mb-2">{song.artist}</p>
            <pre className="text-xs text-gray-300 bg-[#2b2d3a] p-2 rounded-md overflow-x-auto">
              {song.details}
            </pre>

            <button
              onClick={() => {
                const audio = new Audio(
                  "http://127.0.0.1:8000/separated/mixed_output.wav"
                );
                audio.play();
              }}
              className="mt-3 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md text-sm"
            >
              Play
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
