import React, { useState } from "react";
import Mixer from "./Mixer";
import MySongs from "./MySongs";

export default function App() {
  const [tab, setTab] = useState("mixer");

  return (
    <div className="min-h-screen bg-[#0d1020] text-gray-100 p-6">
      <h1 className="text-2xl font-semibold mb-4">ðŸŽ§ Human Audio Mixer</h1>

      {/* Tabs */}
      <div className="flex gap-3 mb-6">
        <button
          onClick={() => setTab("mixer")}
          className={`px-4 py-2 rounded-md ${tab === "mixer" ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-200 hover:bg-gray-600"}`}
        >
          Mixer
        </button>
        <button
          onClick={() => setTab("mysongs")}
          className={`px-4 py-2 rounded-md ${tab === "mysongs" ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-200 hover:bg-gray-600"}`}
        >
          My Songs
        </button>
      </div>

      {/* Pages */}
      {tab === "mixer" && <Mixer onSave={() => setTab("mysongs")} />}
      {tab === "mysongs" && <MySongs />}
    </div>
  );
}
