import React, { useState, useRef } from "react";

const channels = [
  { key: "vocals", label: "Vocals" },
  { key: "drums", label: "Drums" },
  { key: "bass", label: "Bass" },
  { key: "other", label: "Other" },
];

const fmt = (db) => (db > 0 ? `+${db}` : `${db}`) + " dB";

export default function Mixer({ onSave }) {
  const [gains, setGains] = useState(
    Object.fromEntries(channels.map((c) => [c.key, 0]))
  );
  const [file, setFile] = useState(null);
  const [separating, setSeparating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [playing, setPlaying] = useState(false);
  const [paused, setPaused] = useState(false);

  const audioRef = useRef(null);

  const handleChange = (key, val) =>
    setGains((prev) => ({ ...prev, [key]: parseInt(val) }));

  // ------------------------
  // Upload file and run Demucs
  // ------------------------
  const handleUpload = async (e) => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f);
    setSeparating(true);
    setProgress(0);

    const formData = new FormData();
    formData.append("file", f);
    formData.append("username", "demo");

    try {
      const startRes = await fetch("http://127.0.0.1:8000/start_separation", {
        method: "POST",
        body: formData,
      });
      const { session_id } = await startRes.json();

      const es = new EventSource(`http://127.0.0.1:8000/progress/${session_id}`);
      es.onmessage = (ev) => {
        const val = parseInt(ev.data);
        setProgress(val);
        if (val >= 100) {
          es.close();
          setSeparating(false);
          alert("Separation complete!");
        }
      };
      es.onerror = () => {
        es.close();
        setSeparating(false);
      };
    } catch (err) {
      console.error("Upload error:", err);
      setSeparating(false);
      alert("Error starting separation");
    }
  };

  // ------------------------
  // Play mixed output
  // ------------------------
  const playMix = async () => {
    try {
      const formData = new FormData();
      formData.append("username", "demo");
      formData.append("vocals_gain", gains.vocals);
      formData.append("drums_gain", gains.drums);
      formData.append("bass_gain", gains.bass);
      formData.append("other_gain", gains.other);

      const resp = await fetch("http://127.0.0.1:8000/mix", {
        method: "POST",
        body: formData,
      });
      const data = await resp.json();
      const mixUrl = `http://127.0.0.1:8000${data.path}`;

      const audio = new Audio(mixUrl);
      audioRef.current = audio;
      audio.play();
      setPlaying(true);
      setPaused(false);

      audio.onended = () => {
        setPlaying(false);
        setPaused(false);
      };
    } catch (err) {
      console.error("Mix playback error:", err);
      alert("Error mixing stems");
    }
  };

  const pauseMix = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      setPaused(true);
    }
  };

  const resumeMix = () => {
    if (audioRef.current) {
      audioRef.current.play();
      setPaused(false);
    }
  };

  const stopMix = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
      setPlaying(false);
      setPaused(false);
    }
  };

  const handleSave = () => {
    if (!file) {
      alert("Please upload a file first.");
      return;
    }
    const title = file.name.replace(/\.[^/.]+$/, "");
    const details = channels
      .map((c) => `${c.label}: ${fmt(gains[c.key])}`)
      .join("\n");
    onSave?.({ title, artist: "Custom Mix", details });
  };

  return (
    <div className="bg-[#1c1f2b] text-gray-100 p-6 rounded-xl shadow-md w-full max-w-3xl mx-auto">
      <h2 className="text-lg font-semibold mb-4 text-center">
        Audio Mixer
      </h2>

      {/* Upload */}
      <div className="flex flex-col sm:flex-row items-center gap-3 mb-4">
        <input
          type="file"
          accept="audio/*"
          onChange={handleUpload}
          className="text-sm bg-[#2a2d3e] border border-gray-600 p-2 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        {file && (
          <p className="text-xs text-gray-400 truncate">{file.name}</p>
        )}
      </div>

      {/* Progress bar */}
      {separating && (
        <div className="w-full mb-6">
          <div className="w-full bg-gray-700 rounded-md h-2 overflow-hidden">
            <div
              className="h-full bg-blue-400 transition-all duration-300"
              style={{ width: `${progress}%` }}
            ></div>
          </div>
          <p className="text-xs text-gray-400 mt-1 text-center">
            Separating... {progress}%
          </p>
        </div>
      )}

      {/* Sliders */}
      <div className="grid grid-cols-2 sm:grid-cols-4 gap-6 mb-6">
        {channels.map((ch) => (
          <div
            key={ch.key}
            className="flex flex-col items-center bg-[#2a2d3e] p-3 rounded-md border border-gray-700"
          >
            <span className="font-medium mb-2">{ch.label}</span>
            <input
              type="range"
              min="-24"
              max="12"
              value={gains[ch.key]}
              onChange={(e) => handleChange(ch.key, e.target.value)}
              className="w-32"
            />
            <p className="text-xs mt-1">{fmt(gains[ch.key])}</p>
          </div>
        ))}
      </div>

      {/* Buttons */}
      <div className="flex flex-wrap justify-center gap-3">
        {!playing && (
          <button
            onClick={playMix}
            className="px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md"
          >
            Play Mix
          </button>
        )}
        {playing && !paused && (
          <button
            onClick={pauseMix}
            className="px-4 py-2 bg-yellow-400 hover:bg-yellow-500 text-black rounded-md"
          >
            Pause
          </button>
        )}
        {paused && (
          <button
            onClick={resumeMix}
            className="px-4 py-2 bg-green-400 hover:bg-green-500 text-black rounded-md"
          >
            Resume
          </button>
        )}
        {playing && (
          <button
            onClick={stopMix}
            className="px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-md"
          >
            Stop
          </button>
        )}
        <button
          onClick={handleSave}
          className="px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-md"
        >
          Save Mix
        </button>
      </div>
    </div>
  );
}
